import axios from "axios";
import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { Link, useNavigate } from "react-router-dom";
import { login } from "../redux/UserSlice";
import { setTasks } from "../redux/TaskSlice";

const Login = () => {
    const dispatch = useDispatch();
    const navigate = useNavigate();
    const [formData, setFormData] = useState({
        email: "",
        password: ""
    });

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await axios.post('http://localhost:8000/Authentication/login', formData);
            const userData = response.data;
            console.log(userData);
            localStorage.setItem('userId', userData.existinguser._id);
            dispatch(login(userData));
            dispatch(setTasks(userData.tasks));
            navigate('/');
        } catch (error) {
            console.error("Error in login", error);
        }
    };

    return (
        <div className="form-container">
            <form onSubmit={handleSubmit}>
                <h1>Login Form</h1>
                <label htmlFor="email">Email</label>
                <input type="text" name="email" onChange={handleChange} placeholder="Enter your email" />
                <label htmlFor="password">Password</label>
                <input type="password" name="password" onChange={handleChange} placeholder="Enter your password" />
                <div>
                    <input type="checkbox" /><label htmlFor="" className="terms-label">I accept the terms and conditions</label>
                </div>
                <button type="submit">Login</button>
                <span>Don't have an account?
                    <Link to="/signup"> Signup</Link>
                </span>
            </form>
        </div>
    );
};

export default Login;
